class Solution(object):
    def longestPalindrome(self, s):
        """
        :type s: str
        :rtype: str
        """
        L = len(s)
        ret_len, ret_start = 1, 0
        for i in xrange(L):
            left = right = i
            while 0 <= left and right < L and s[left] == s[right]:
                left -= 1
                right += 1
            else:
                if ret_len < right - left - 1:
                    ret_len = right - left - 1
                    ret_start = left + 1
            left, right = i, i+1
            while 0 <= left and right < L and s[left] == s[right]:
                left -= 1
                right += 1
            else:
                if ret_len < right - left - 1:
                    ret_len = right - left - 1
                    ret_start = left + 1
        return s[ret_start: ret_start + ret_len]


if __name__ == '__main__':
    print(Solution().longestPalindrome(""))
    print(Solution().longestPalindrome(""))
    print(Solution().longestPalindrome("a"))
